mapper.select:
select count(*)
from submission
  INNER JOIN ct_sn
      ON submission.id = ct_sn.snId
         AND ct_sn.ctId = #{ctId}
WHERE submission.deleteFlg = '0'
    AND submission.pmId=#{pmId}
    AND submission.urId=#{urId}
    AND submission.judgeStatus=6

SELECT submission.id AS id, problem.id AS pmId, problem.name AS pmNm, user.id AS urId, user.fullname AS fullname, submission.memoryUsed AS memoryUsed, submission.timeRun AS timeRun, submission.createTime AS createTime, submission.judgeStatus AS judgeStatus, submission.verdict AS verdict, language.name AS leNm FROM submission INNER JOIN ct_sn ON submission.id = ct_sn.snId AND ct_sn.ctId = #{ctId} INNER JOIN user on submission.urId = user.id AND user.deleteFlg = '0'
AND user.handle=#{handle}
LEFT JOIN problem ON submission.pmId = problem.id AND submission.deleteFlg = '0' LEFT JOIN language ON submission.leId = language.id AND language.deleteFlg = '0' WHERE submission.deleteFlg = '0' ORDER BY submission.createTime DESC

SELECT submission.id AS id, problem.id AS pmId, problem.name AS pmNm, user.id AS urId, user.fullname AS fullname, submission.memoryUsed AS memoryUsed, submission.timeRun AS timeRun, submission.createTime AS createTime, submission.judgeStatus AS judgeStatus, submission.verdict AS verdict, language.name AS leNm FROM submission INNER JOIN ce_sn ON submission.id = ce_sn.snId AND ce_sn.ceId = #{ceId} INNER JOIN user on submission.urId = user.id AND user.deleteFlg = '0'
AND user.handle=#{handle}
LEFT JOIN problem ON submission.pmId = problem.id AND submission.deleteFlg = '0' LEFT JOIN language ON submission.leId = language.id AND language.deleteFlg = '0' WHERE submission.deleteFlg = '0' ORDER BY submission.createTime DESC
